// ScenarioImage.js
import React, { useState } from 'react';

import './Scene.css';

const ScenarioImage = ({ image, maxWidth, maxHeight, interactiveAreas, handleMouseOver, handleMouseOut }) => (
  <div className="image-container" onMouseOut={handleMouseOut}>
    <img
      src={image}
      alt="Scenario Image"
      style={{
        maxWidth,
        maxHeight,
      }}
      className="scenario-image"
    />
    {interactiveAreas.map((area) => (
      <div
        key={area.id}
        className="interactive-area"
        onMouseOver={() => handleMouseOver(area)}
        style={{
          position: 'absolute',
          top: `${area.coords[1]}px`,
          left: `${area.coords[0]}px`,
          width: `${area.coords[2] - area.coords[0]}px`,
          height: `${area.coords[3] - area.coords[1]}px`,
          cursor: 'pointer',
          backgroundColor: area.color,
        }}
      />
    ))}
  </div>
);

export default ScenarioImage;
