import React, { useState } from 'react';
import Room from './Room';

const TextAdventureGame = () => {
  const [currentRoomIndex, setCurrentRoomIndex] = useState(0);

  const handleChoice = (choiceIndex) => {
    const nextRoomIndex = rooms[currentRoomIndex].choices[choiceIndex].nextRoomIndex;
    setCurrentRoomIndex(nextRoomIndex);
  };

  const currentRoom = rooms[currentRoomIndex];

  return <Room room={currentRoom} onChoice={handleChoice} />;
};

const rooms = [
  {
    description: 'You are in Room 1. There are two doors.',
    image: '/images/image1.jpg',
    choices: [
      { text: 'Open the left door', nextRoomIndex: 1 },
      { text: 'Open the right door', nextRoomIndex: 2 },
    ],
  },
  {
    description: 'You are in Room 2. There is a staircase going up.',
    image: '/images/image2.jpg',
    choices: [
      { text: 'Take the staircase', nextRoomIndex: 3 },
      { text: 'Go back to Room 1', nextRoomIndex: 0 },
    ],
  },
  {
    description: 'You are in Room 3. It is a dark room with a locked chest.',
    image: '/images/image3.jpg',
    choices: [
      { text: 'Try to open the chest', nextRoomIndex: 4 },
      { text: 'Go back downstairs', nextRoomIndex: 2 },
    ],
  },
  {
    description: 'You found a key in the chest. There is a door to the next room.',
    image: '/images/image4.jpg',
    choices: [
      { text: 'Use the key on the door', nextRoomIndex: 2 },
    ],
  },
  {
    description: 'Congratulations! You have escaped the dungeon.',
    image: '/images/default.jpg',
    choices: [],
  },
];

export default TextAdventureGame;
