// TextAdventureGame.js
import React, { useState, useEffect } from 'react';

const TextAdventureGame = ({ onStart }) => {
  const [scenario, setScenario] = useState(null);

  useEffect(() => {
    // Load initial scenario or perform any setup
    setScenario({
      description1: 'You are at the entrance of a mysterious cave.',
      description2: 'There are two paths ahead. Choose wisely.',
      choices: ['Enter the left path', 'Enter the right path'],
      images: ['/images/image1.jpg', '/images/image2.jpg'],
    });
  }, []);

  const handleChoice = (choice) => {
    // Handle different scenarios based on player choices
    let newScenario;
    switch (choice) {
      case 'Enter the left path':
        newScenario = {
          description1: 'You venture into the left path.',
          description2: 'There is a hidden treasure room.',
          choices: ['Take the treasure', 'Leave the room'],
          images: ['/images/image3.jpg'], // Image for this scenario
        };
        break;
      case 'Enter the right path':
        newScenario = {
          description1: 'You choose the right path.',
          description2: 'You encounter a fierce dragon.',
          choices: ['Fight the dragon', 'Run away'],
          images: ['/images/image4.jpg'], // Image for this scenario
        };
        break;
      // Handle other choices and scenarios
      default:
        newScenario = {
          description1: 'You make a choice, but nothing interesting happens.',
          description2: 'Try again.',
          choices: ['Try again'],
          images: ['/images/default-image.jpg'], // Default image
        };
    }
    setScenario(newScenario);
  };

  return (
    <div>
      {scenario && (
        <>
          <h2>{scenario.description1}</h2>
          <h1>{scenario.description2}</h1>
          <img src={scenario.images[0]} alt="Scenario Image" style={{ maxWidth: '100%' }} />
          <ul>
            {scenario.choices.map((choice, index) => (
              <li key={index}>
                <button onClick={() => handleChoice(choice)}>{choice}</button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default TextAdventureGame;
