// Scene.js
import React, { useState } from 'react';
import ScenarioDescription from './ScenarioDescription';
import ScenarioImage from './ScenarioImage';
import Choices from './Choices';
import './Scene.css';

const Scene = ({ scene, onChoice }) => {
  const [showTooltip, setShowTooltip] = useState(false);

  const handleMouseOver = () => {
    setShowTooltip(true);
  };

  const handleMouseOut = () => {
    setShowTooltip(false);
  };

  const isMainRoomImage = scene.image === '/images/mainroom.jpg'; // Adjust the image path

  return (
    <div className="scene-container">
      <h3 dangerouslySetInnerHTML={{ __html: scene.description }}></h3>
      <div
        className="image-container"
        onMouseOver={isMainRoomImage ? handleMouseOver : undefined}
        onMouseOut={isMainRoomImage ? handleMouseOut : undefined}
      >
        <img
          src={scene.image}
          alt="Scenario Image"
          style={{
            maxWidth: 400,
            maxHeight: 400,
          }}
          className="scenario-image"
        />
        {isMainRoomImage && showTooltip && (
          <div className="tooltip">
            <p>This is an interactive area!</p>
          </div>
        )}
      </div>
      <Choices choices={scene.choices} onChoice={onChoice} />
    </div>
  );
};

export default Scene;
