import React from 'react';
import TextAdventureGame from './components/TextAdventureGame';
import InteractiveImageMap from './InteractiveImageMap';

const App = () => {
  const [selectedArea, setSelectedArea] = useState(null);

  const handleAreaClick = (areaId) => {
    // Handle the click event based on the areaId
    console.log(`Clicked area: ${areaId}`);
    // Add your game logic here
    setSelectedArea(areaId);
  };

  return (
    <div>
      <h1>Your Text-Based Adventure Game</h1>

      {/* Add other game components or UI here */}
      <InteractiveImageMap onAreaClick={handleAreaClick} />
      <TextAdventureGame selectedArea={selectedArea} />
    </div>
  );
};

export default App;
