// ScenarioImage.js
import React from 'react';
import './Scene.css';

const TextPopup = ({ text }) => (
  <div className="text-popup">
    <p>{text}</p>
  </div>
);

const SquareOverlay = ({ onHover }) => (
  <div className="square-overlay" onMouseOver={onHover}></div>
);

const ScenarioImage = ({ image, maxWidth, maxHeight, popupText }) => (
  <div className="image-container">
    <img
      src={image}
      alt="Scenario Image"
      style={{
        maxWidth,
        maxHeight,
      }}
      className="scenario-image"
    />
    <SquareOverlay onHover={() => console.log('Mouse over!')} />
    {popupText && <TextPopup text={popupText} />}
  </div>
);

export default ScenarioImage;
