// Scene.js
import React from 'react';
import ScenarioDescription from './ScenarioDescription';
import ScenarioImage from './ScenarioImage';
import Choices from './Choices';
import ImageWithTooltip from './ImageWithTooltip'; // Import the ImageWithTooltip component
import './Scene.css';

const MainRoomTooltipAreas = [
  {
    id: 'area1',
    name: 'Desk Drawer',
    shape: 'rect',
    coords: [50, 100, 150, 200], // Adjust the coordinates as needed
    preFillColor: 'transparent',
    tooltip: 'You find some documents in the desk drawer.',
  },
  // Add more areas as needed
];

const Scene = ({ scene, onChoice }) => (
  <div className="scene-container">
    <h3 dangerouslySetInnerHTML={{ __html: scene.description }}></h3>
    <ScenarioImage image={scene.image} />
    <Choices choices={scene.choices} onChoice={onChoice} />
    {scene.showMainRoomImage && (
      <ImageWithTooltip
        image='/images/mainroom.jpg'
        tooltipAreas={MainRoomTooltipAreas}
      />
    )}
  </div>
);

export default Scene;
