import React, { useState } from 'react';

const TextAdventureGame = () => {
  const [scenario, setScenario] = useState({
    description: 'Welcome to the most amazing and TOP rated Escape Text based Adventure Game! \n You are in a room.',
    choices: ['Open the door', 'Look out the window'],
  });

  const handleChoice = (choice) => {
    switch (choice) {
      case 'Open the door':
        setScenario({
          description: 'You open the door and find a key on the floor.',
          choices: ['Pick up the key', 'Explore the room further'],
        });
        break;
      case 'Look out the window':
        setScenario({
          description: 'You see a beautiful landscape outside.',
          choices: ['Go back inside', 'Jump out the window'],
        });
        break;
      case 'Pick up the key':
        setScenario({
          description: 'You now have the key in your inventory.',
          choices: ['Explore the room further', 'Use the key on the locked chest'],
        });
        break;
      case 'Explore the room further':
        setScenario({
          description: 'You find a mysterious book on the shelf.',
          choices: ['Read the book', 'Ignore the book'],
        });
        break;
      case 'Use the key on the locked chest':
        setScenario({
          description: 'The chest opens, revealing a treasure!',
          choices: ['Celebrate your victory', 'Continue exploring'],
        });
        break;
      // Add more cases for other choices and scenarios
      default:
        setScenario({
          description: 'You make a choice, but nothing interesting happens.',
          choices: ['Try again'],
        });
    }
  };

  return (
    <div>
      <p>{scenario.description}</p>
      <ul>
        {scenario.choices.map((choice, index) => (
          <li key={index}>
            <button onClick={() => handleChoice(choice)}>{choice}</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TextAdventureGame;
