// ScenarioImage.js
import React, { useRef } from 'react';
import './Scene.css';

const ScenarioImage = ({ image, maxWidth, maxHeight }) => {
  const particleContainerRef = useRef(null);

  const handleMouseOver = (event) => {
    const { offsetX, offsetY } = event.nativeEvent;
    const particles = particleContainerRef.current;

    const particle = document.createElement('div');
    particle.className = 'particle';
    particle.style.setProperty('--tx', offsetX);
    particle.style.setProperty('--ty', offsetY);

    particles.appendChild(particle);
    setTimeout(() => {
      particle.remove();
    }, 1000);
  };

  return (
    <div className="image-container" onMouseOver={handleMouseOver}>
      <img
        src={image}
        alt="Scenario Image"
        style={{
          maxWidth,
          maxHeight,
        }}
        className="scenario-image"
      />
      <div className="particle-effect" ref={particleContainerRef}></div>
    </div>
  );
};

export default ScenarioImage;
