// Room.js
import React from 'react';
import ScenarioDescription from './ScenarioDescription';
import ScenarioImage from './ScenarioImage';
import Choices from './Choices';

const Room = ({ room, onChoice }) => (
  <div>
    <ScenarioDescription description={room.description} />
    <ScenarioImage image={room.image} />
    <Choices choices={room.choices} onChoice={onChoice} />
  </div>
);

export default Room;
