// Scene.js
import React, { useState } from 'react';
import ScenarioDescription from './ScenarioDescription';
import ScenarioImage from './ScenarioImage';
import Choices from './Choices';
import './Scene.css';

const Scene = ({ scene, onChoice }) => {
  const [showTooltip, setShowTooltip] = useState(false);
  const [tooltipText, setTooltipText] = useState('');
  const [activeArea, setActiveArea] = useState(null);

  const handleMouseOver = (area) => {
    setShowTooltip(true);
    setActiveArea(area);
    updateTooltipText(area);
  };

  const handleMouseOut = () => {
    setShowTooltip(false);
    setActiveArea(null);
  };

  const isMainRoomImage = scene.image === '/images/mainroom.jpg';
  
  let interactiveAreas = [];

  if (isMainRoomImage) {
    interactiveAreas = [
      {
        id: 'area1',
        coords: [50, 100, 150, 200],
        tooltip: 'Find an EXIT',
        color: 'rgba(255, 0, 0, 0.3)', // Red color with transparency
      },
      {
        id: 'area2',
        coords: [250, 150, 300, 200],
        tooltip: 'Second Area',
        color: 'rgba(0, 0, 255, 0.3)', // Blue color with transparency
      },
    ];
  }

  const updateTooltipText = (area) => {
    if (isMainRoomImage) {
      setTooltipText(area.id === 'area1' ? 'Text 1' : area.id === 'area2' ? 'Text 2' : '');
    } else {
      setTooltipText(area ? area.tooltip : 'This is an interactive area!');
    }
  };

  return (
    <div className="scene-container">
      <h3 dangerouslySetInnerHTML={{ __html: scene.description }}></h3>
      <div
        className="image-container"
        onMouseOut={handleMouseOut}
      >
        <img
          src={scene.image}
          alt="Scenario Image"
          style={{
            maxWidth: 400,
            maxHeight: 400,
          }}
          className="scenario-image"
        />
        {interactiveAreas.map((area) => (
          <div
            key={area.id}
            className="interactive-area"
            onMouseOver={() => handleMouseOver(area)}
            style={{
              position: 'absolute',
              top: `${area.coords[1]}px`,
              left: `${area.coords[0]}px`,
              width: `${area.coords[2] - area.coords[0]}px`,
              height: `${area.coords[3] - area.coords[1]}px`,
              cursor: 'pointer',
              backgroundColor: area.color,
              border: activeArea === area ? '2px solid #000' : 'none',
            }}
          />
        ))}
        {showTooltip && (
          <div className="tooltip">
            <p>{tooltipText}</p>
          </div>
        )}
      </div>
      <Choices choices={scene.choices} onChoice={onChoice} />
    </div>
  );
};

export default Scene;
