// ImageWithTooltip.js
import React from 'react';
import ReactTooltip from 'react-tooltip';
import { ImageMapper } from 'react-image-mapper';
import './ImageWithTooltip.css'; 

const ImageWithTooltip = ({ image, tooltipAreas }) => {
  const handleAreaMouseEnter = (area) => {
    ReactTooltip.show(area.id);
  };

  const handleAreaMouseLeave = () => {
    ReactTooltip.hide();
  };

  return (
    <div>
      <ImageMapper
        src={image}
        map={tooltipAreas}
        onMouseEnter={handleAreaMouseEnter}
        onMouseLeave={handleAreaMouseLeave}
      />
      {tooltipAreas.map((area) => (
        <ReactTooltip
          key={area.id}
          id={area.id}
          type="light"
          effect="solid"
        >
          {area.tooltip}
        </ReactTooltip>
      ))}
    </div>
  );
};

export default ImageWithTooltip;
