// Scene.js
import React, { useState } from 'react';
import ScenarioDescription from './ScenarioDescription';
import ScenarioImage from './ScenarioImage';
import Choices from './Choices';
import './Scene.css';

const Scene = ({ scene, onChoice }) => {
  const [showTooltip, setShowTooltip] = useState(false);
  const [tooltipText, setTooltipText] = useState('');

  const handleMouseOver = (areaTooltip) => {
    setShowTooltip(true);
    setTooltipText(areaTooltip);
  };

  const handleMouseOut = () => {
    setShowTooltip(false);
  };

  const isMainRoomImage = scene.image === '/images/mainroom.jpg';
  const isSkeletonScene = scene.image === '/images/skeleton.jpg';

  const interactiveAreas = [
    {
      id: 'area1',
      coords: [50, 100, 150, 200],
      tooltip: 'Find an EXIT',
    },
    {
      id: 'area2',
      coords: [250, 150, 300, 200],
      tooltip: 'Another interactive area',
    },
  ];

  const updateTooltipText = () => {
    if (isMainRoomImage && scene.description.includes('Scene 1')) {
      setTooltipText('Find an EXIT');
    } else if (isSkeletonScene && scene.description.includes('Scene 3')) {
      setTooltipText('DEAD');
    } else {
      setTooltipText('This is an interactive area!');
    }
  };

  return (
    <div className="scene-container">
      <h3 dangerouslySetInnerHTML={{ __html: scene.description }}></h3>
      <div className="image-container">
        <img
          src={scene.image}
          alt="Scenario Image"
          style={{
            maxWidth: 400,
            maxHeight: 400,
          }}
          className="scenario-image"
        />
        {isMainRoomImage &&
          interactiveAreas.map((area) => (
            <div
              key={area.id}
              className="interactive-area"
              onMouseOver={() => handleMouseOver(area.tooltip)}
              onMouseOut={handleMouseOut}
              style={{
                position: 'absolute',
                top: `${area.coords[1]}px`,
                left: `${area.coords[0]}px`,
                width: `${area.coords[2] - area.coords[0]}px`,
                height: `${area.coords[3] - area.coords[1]}px`,
                cursor: 'pointer',
              }}
            />
          ))}
        {showTooltip && (
          <div className="tooltip">
            <p>{tooltipText}</p>
          </div>
        )}
      </div>
      <Choices choices={scene.choices} onChoice={onChoice} />
    </div>
  );
};

export default Scene;
