// Scene.js
import React, { useState } from 'react';
import ScenarioDescription from './ScenarioDescription';
import ScenarioImage from './ScenarioImage';
import Choices from './Choices';
import './Scene.css';

const Scene = ({ scene, onChoice }) => {
  const [showTooltip, setShowTooltip] = useState(false);

  const handleMouseOver = (e) => {
    // Define the coordinates of the square area in the middle of the image
    const squareSize = 100;
    const imageWidth = e.target.clientWidth;
    const imageHeight = e.target.clientHeight;
    const squareTop = (imageHeight - squareSize) / 2;
    const squareBottom = squareTop + squareSize;
    const squareLeft = (imageWidth - squareSize) / 2;
    const squareRight = squareLeft + squareSize;

    // Check if the mouse is within the defined square area
    if (
      e.nativeEvent.offsetY >= squareTop &&
      e.nativeEvent.offsetY <= squareBottom &&
      e.nativeEvent.offsetX >= squareLeft &&
      e.nativeEvent.offsetX <= squareRight
    ) {
      setShowTooltip(true);
    }
  };

  const handleMouseOut = () => {
    setShowTooltip(false);
  };

  return (
    <div className="scene-container">
      <h3 dangerouslySetInnerHTML={{ __html: scene.description }}></h3>
      <div
        className="image-container"
        onMouseOver={handleMouseOver}
        onMouseOut={handleMouseOut}
      >
        <img
          src={scene.image}
          alt="Scenario Image"
          style={{
            maxWidth: 400,
            maxHeight: 400,
          }}
          className="scenario-image"
        />
        {showTooltip && (
          <div className="tooltip">
            <p>This is an interactive area!</p>
          </div>
        )}
      </div>
      <Choices choices={scene.choices} onChoice={onChoice} />
    </div>
  );
};

export default Scene;
