// src/components/scenesData.js

const scenes = [
    {
      description: 'Scene 1 in Room 1 MAIN ...',
      image: '/images/mainroom.jpg',
      interactiveAreas: [
        {
          id: 'area1',
          coords: [50, 100, 150, 200],
          tooltip: 'This is the first area!',
        },
        {
          id: 'area2',
          coords: [250, 150, 300, 200],
          tooltip: 'This is the second area!',
        },
        // Add more areas as needed
      ],
      choices: [
        // ... your choices
      ],
    },
    // Other scenes
  ];
  
  export default scenes;
