// ScenarioImage.js
import React from 'react';
import './Scene.css';

const SquareOverlay = ({ size }) => (
  <div className="square-overlay" style={{ width: size, height: size }}></div>
);

const ScenarioImage = ({ image, maxWidth, maxHeight, overlaySize }) => (
  <div className="image-container">
    <img
      src={image}
      alt="Scenario Image"
      style={{
        maxWidth,
        maxHeight,
      }}
      className="scenario-image"
    />
    <SquareOverlay size={overlaySize} />
  </div>
);

export default ScenarioImage;
